package controllers.eeda;import java.util.List;import models.UserLogin;import com.jfinal.core.Controller;import com.jfinal.log.Log;import com.jfinal.plugin.activerecord.Db;import com.jfinal.plugin.activerecord.Record;import controllers.profile.LoginUserController;public class ListConfigController extends Controller {    private static Log logger = Log.getLog(ListConfigController.class);      public void list() {        UserLogin user = LoginUserController.getLoginUser(this);        long user_id = user.getLong("id");                String module_id = getPara("module_id");                List<Record> conList = Db.find("select * from order_cols_config "                + "where user_id=? and module_id=? order by seq", user_id, module_id);        renderJson(conList);    }}